DROP TABLE IF EXISTS restaurant_list;
DROP SEQUENCE IF EXISTS seq_restaurant_id;

CREATE SEQUENCE seq_restaurant_id
  INCREMENT BY 1
  NO MAXVALUE
  NO MINVALUE
  CACHE 1;


CREATE TABLE restaurant_list (
	restaurant_id int DEFAULT nextval('seq_restaurant_id'::regclass) NOT NULL,
	restaurant_name varchar(50),
	address varchar(100),
	cuisine varchar(50),
	zip_code int,
	city varchar(50),
	phone_number varchar(12),
	delivery varchar (3), 
	CONSTRAINT PK_restaurant PRIMARY KEY (restaurant_id)
);





SELECT * FROM restaurant_list;
